"use strict";
import {
    menuTriggerFunction
} from "../components/mobileMenuTrigger.js";
import {
    responsive_header
} from "../utils/responsive-header.js";
const header = document.getElementById("header");
const show_menu = document.getElementById("show-menu");
const menuTrigger = document.getElementById("menu-trigger--btn");
const menuBtnStyle = document.getElementById("menu-btn--style");
const head_wrapper = document.getElementById("head-wrapper");
const MENU_FLAG = window.getComputedStyle(show_menu);
const certificate = document.getElementById("cert");
const certificatePhone = document.getElementById("certPhone");
const overlay_window = document.getElementById("overlay-window");
const cert_icon = document.getElementById("cert-icon");
menuTrigger.addEventListener("click", (() => {
    menuTriggerFunction(MENU_FLAG, show_menu, menuBtnStyle)
}));
cert_icon.addEventListener("click", (() => {
    overlay_window.classList.add("show");
    if (window.innerWidth <= 480) {
        certificatePhone.style.display = "block"
    } else {
        certificate.style.display = "block"
    }
}));
document.addEventListener("click", (event => {
    const isOverlayVisible = overlay_window.style.display === "flex" || overlay_window.style.display === "";
    const clickedInsideCert = certificate.contains(event.target);
    const clickedIcon = cert_icon.contains(event.target);
    if (isOverlayVisible && !clickedInsideCert && !clickedIcon) {
        overlay_window.classList.remove("show");
        certificate.style.display = "none"
    }
}));
responsive_header(head_wrapper, show_menu);
window.addEventListener("resize", responsive_header);