"use strict";
import {
    menuTriggerFunction
} from "../components/mobileMenuTrigger.min.js";
import {
    init_service_item
} from "../components/index_services.min.js";
import {
    displayServiceData
} from "../components/displayServiceData.min.js";
import {
    responsive_header
} from "../utils/responsive-header.min.js";
const header = document.getElementById("header"),
    head_wrapper = document.getElementById("head-wrapper"),
    show_menu = document.getElementById("show-menu"),
    menuTrigger = document.getElementById("menu-trigger--btn"),
    menuBtnStyle = document.getElementById("menu-btn--style"),
    MENU_FLAG = window.getComputedStyle(show_menu),
    servicesContainer = document.getElementsByClassName("services-wrappers"),
    body = document.getElementById("body");
async function fetchData() {
    try {
        const e = await fetch("https://hors-backend.onrender.com/api/v1/services");
        if (!e.ok) throw new Error("Network error: " + e + ".");
        const n = await e.json();
        displayServiceData(n.services.service, servicesContainer)
    } catch (e) {
        console.error(e)
    }
}
menuTrigger.addEventListener("click", (() => {
    menuTriggerFunction(MENU_FLAG, show_menu, menuBtnStyle)
})), init_service_item(servicesContainer), responsive_header(head_wrapper, show_menu), window.addEventListener("resize", () => responsive_header(head_wrapper, show_menu));
window.onload = fetchData;