"use strict";

function initMap() {
    const o = {
            lat: 46.02322,
            lng: 14.540815
        },
        e = new google.maps.Map(document.getElementById("map"), {
            zoom: 14,
            center: o
        }),
        n = new google.maps.Marker({
            position: o,
            map: e,
            title: "Varstvo psov, sprehajanje in šolanje | Hors.si – Ljubljana",
            icon: {
                url: "../../assets/hors-logo--map.png",
                scaledSize: new google.maps.Size(35, 35)
            }
        }),
        s = new google.maps.InfoWindow({
            content: "<strong>Varstvo psov, sprehajanje in šolanje | Hors.si – Ljubljana</strong>"
        });
    n.addListener("mouseover", (() => s.open({
        anchor: n,
        map: e,
        shouldFocus: !1
    }))), n.addListener("mouseout", (() => s.close()))
}